import { Component } from "react";
import CartBike from './CartBike.js'
import '../App.css'
import axios from "axios"

const styles = {
	cartBox: {
		display: 'flex',
		backgroundColor: '#FCFCFC',
		position:'absolute',
		boxShadow: '1px 5px 5px rgb(0,0,0,0.3)',
		borderRadius: '5px',
		width: '300px',
		left: 900,
		top: 90,
		paddingTop: 25
	},
	ul: {
		listStyleType: 'none'
	}
}

const handleSubmit = async (e) => {
	e.preventDefault();
	const { data } = await axios.post('/create-checkout-session',
	{
		totalPrice: totalPrice
	})
	console.log(data)
}

class CartBox extends Component{
	
	render(){
		const { cart, deleteBike, isBikeInCart, totalPrice } = this.props
		
		return(
			<div style={styles.cartBox}>
				{<ul style={styles.ul}>
					{cart.map(cartBike => 
						<li key={cartBike.time}><CartBike 
						cartBike={cartBike}
						deleteBike={deleteBike}
						isBikeInCart={isBikeInCart}
						/></li>
					)}
					<li>{`Total: ${totalPrice}€`}</li>
        			{/*<Button onClick={()=>showCheckoutForm()}>Book</Button>*/}
					<form onSubmit={handleSubmit}>
						<button type="submit" class="btn btn-outline-dark btn-sm" id="paybtn">
							Pay
						</button>
					</form>
					
				</ul>}
			</div>
		)
	}

}

export default CartBox
