import { Component } from "react";
import Button from "./Button";

const styles = {
	cartBike: {
		display: 'flex',
		alignItems: 'center',
		justifyContent: 'space-between',
		position: 'relative'
	},
	time: {
		position: 'absolute',
		right: 90,
		top: 50
	},
	quantity: {
		position: 'absolute',
		right: 60,
		top: 50
	},
	price: {
		position: 'absolute',
		right: 30,
		top: 50
	},
	button: {
		position: 'absolute',
		right: 0,
		top: 50
	}
}

class CartBike extends Component {
	render(){
		const { deleteBike, cartBike } = this.props
		return(
			<div style={styles.cartBike}>
				{/*cartBike.isCartInBike?(cartBike.time)(cartBike.quantity)
				:<Button onClick={()=>deleteBike(cartBike)}>Delete</Button>*/}
				{cartBike.isBikeInCart?<div><h4 style={styles.time}>{cartBike.time}</h4><h3 style={styles.quantity}>{cartBike.quantity}</h3><h5 style={styles.price}>{cartBike.price}</h5></div>
				: null}
				{cartBike.isBikeInCart?<Button style={styles.button} onClick={()=>deleteBike(cartBike)}>Delete</Button>:null}
			</div>
		)
	}
}

export default CartBike
