import { Component } from "react";
import CartBike from './CartBike'


const styles = {
	cartBox: {
		display: 'flex',
		flexDirection: 'row',
		justifyContent: 'space-between',
		alignItems: 'left',
		backgroundColor: '#fff',
		position: 'absolute',
		marginTop: 30,
		boxShadow: '1px 5px 5px rgb(0,0,0,0.3)',
		borderRadius: '5px',
		width: '300px',
		left: 0,
		top: 0
	},
	ul: {
		listStyleType: 'none'
	},
	cartbike: {
		position: 'absolute',
		listStyleType: 'none',
		padding: '25px 20px',
		borderBottom: 'solid 1px #aaa',
	  },
}

class CartBox extends Component{
	render(){
		const { cart, deleteBike, isBikeInCart, totalPrice } = this.props
		return(
			<div style={styles.cartBox}>

				<ul style={styles.cartbike}>
					{cart.map(cartBike => 
						<li key={cartBike.time}><CartBike 
						cartBike={cartBike}

						deleteBike={deleteBike}
						isBikeInCart={isBikeInCart}
						/></li>
					)}
					<li>{`${totalPrice}€`}</li>
				</ul>


			</div>
		)
	}

}

export default CartBox
