import { Component } from "react";
import Button from "./Button";
import CartBike from './CartBike'


const styles = {
	cartBox: {
		backgroundColor: '#fff',
    position: 'absolute',
    marginTop: 30,
    boxShadow: '1px 5px 5px rgb(0,0,0,0.3)',
    borderRadius: '5px',
    width: '300px',
    right: 50,
	}
}

class CartBox extends Component{
	render(){
		const { cart, deleteBike, isBikeInCart } = this.props
		return(
			<div style={styles.cartBox}>
				{/*<ul>
					{cart.map(element=>
						element.isBikeInCart?<li key={element.time}>
							{element.time} {element.quantity} <Button onClick={()=>{deleteBike(bike)}}>Delete</Button>
						</li>:null)}
					</ul>*/}

				<ul>
					{cart.map(cartBike => {
						<li><CartBike 
						cartBike={cartBike}
						key={cartBike.time}
						deleteBike={deleteBike}
						isBikeInCart={isBikeInCart}
						/></li>
					})}
				</ul>


			</div>
		)
	}

}

export default CartBox
