import { Component } from "react";
import CartBike from './CartBike.js'
import '../App.css'
import axios from "axios"

const styles = {
	cartBox: {
		display: 'flex',
		backgroundColor: '#FCFCFC',
		position:'absolute',
		boxShadow: '1px 5px 5px rgb(0,0,0,0.3)',
		borderRadius: '5px',
		width: '300px',
		left: 900,
		top: 90,
		paddingTop: 25
	},
	ul: {
		listStyleType: 'none'
	}
}



class CartBox extends Component{
	render(){
		const { cart, deleteBike, isBikeInCart, totalPrice, quantity, bookingTime } = this.props
		const handleSubmit = async (e) => {
			e.preventDefault();
			try{
				const {data} = await axios.post('http://localhost:3001/create-checkout-session',
			{
				totalPrice: totalPrice
			},
			)
			console.log(data)
			} catch(error){
				console.log(error)
			}
		}
		return(
			<div style={styles.cartBox}>
				{<ul style={styles.ul}>
					{cart.map(cartBike => 
						<li key={cartBike.time}><CartBike 
						cartBike={cartBike}
						deleteBike={deleteBike}
						isBikeInCart={isBikeInCart}
						/></li>
					)}
					<li>{`Total: ${totalPrice}€`}</li>
        	
					{<form action="http://localhost:3001/create-checkout-session" method="POST">
						<input type="hidden" name="totalPrice" value={totalPrice}/>
						<input type="hidden" name="quantity" value={quantity}/>
						<input type="hidden" name="cart" value={cart}/>
						<button type="submit" class="btn btn-outline-dark btn-sm" id="paybtn">
							Pay
						</button>
					</form>}
				</ul>}
			</div>
		)
	}

}

export default CartBox
