import { Component } from "react";
//import Button from "./Button";

const styles = {
	cartBike: {
		display: 'flex',
		alignItems: 'center',
		justifyContent: 'space-between'
	},
	container: {
		display: 'flex',
		alignItems: 'center',
		justifyContent: 'space-between',
		position: 'relative'
	},
	button: {
		position: 'absolute',
		left: 200
	}
}

class CartBike extends Component {
	render(){
		const { deleteBike, cartBike } = this.props
		return(
			<div style={styles.container}>
				{cartBike.isBikeInCart?<div style={styles.cartBike}><h4>{cartBike.time}</h4><h3>{cartBike.quantity}</h3><h5>{cartBike.price}</h5>
				<button style={styles.button} onClick={()=>deleteBike(cartBike)}>Delete</button></div>
				: null}
				{/*cartBike.isBikeInCart?<Button style={styles.button} onClick={()=>deleteBike(cartBike)}>Delete</Button>:null*/}
			</div>
		)
	}
}

export default CartBike
