/*import React, { useState } from "react";

import {
  CardElement,
  useStripe,
  useElements,
} from "@stripe/react-stripe-js";

import axios from "axios";

const styles = {
	checkoutForm: {
		position:'absolute',
		left: 480,
		top: 90,
	}
}


const CheckoutForm = ({hideFormAndCart, totalPrice, updateCart}) => {
  const stripe = useStripe();
  const elements = useElements();

  const [loading, setLoading] = useState(false);
  //const [totalPrice, setTotalPrice] = useState(null)

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: elements.getElement(CardElement),
    });
    setLoading(true);
	

    if (!error) {
       console.log(paymentMethod)
      const { id } = paymentMethod;
      try {
        const { data } = await axios.post(
          "http://localhost:3001/checkout",
          {
            id,
            amount: 8000, 
          }
        );
        console.log(data);


        elements.getElement(CardElement).clear();
      } catch (error) {
        console.log(error);
      }
      setLoading(false);
    }
  };

  console.log(!stripe || loading);

  return (
    
      <div style={styles.checkoutForm} className="container p-4">
		<div className="row h-100">
          <div className="col-md-4 offset-md-4 h-100">
		  	<form className="card card-body" onSubmit={handleSubmit}>
				<div className="form-group">
					{`The total to pay is ${totalPrice}€`}
					<p><b>Use the card number 4242 4242 4242 4242
						and any expiry date, CVC and PC <br/>
					</b></p>
				
					<CardElement  />
				</div>

				<button disabled={!stripe} className="btn btn-success" onClick={()=>{setTimeout(hideFormAndCart, 4000); updateCart()}}>
				{loading ? (
					<div className="spinner-border text-light" role="status">
						<span className="sr-only">Loading...</span>
					</div>
				) : (
					"Pay"
				)}
				</button>
				</form>
