import Bikes from './components/Bikes'
import { Component } from 'react';

class App extends Component{

	state = {
		bikes: [
		{time: '09:00', quantity: 8, price: '10€'},
		{time: '10:00', quantity: 8, price: '10€'},
		{time: '11:00', quantity: 8, price: '10€'},
		{time: '12:00', quantity: 8, price: '10€'},
		{time: '13:00', quantity: 8, price: '10€'},
		{time: '14:00', quantity: 8, price: '10€'},
		{time: '15:00', quantity: 8, price: '10€'},
		{time: '16:00', quantity: 8, price: '10€'}],
		cart: [],
		isCartVisible: false
	}

	addToCart = (bike) => {
		console.log(bike)
		const {cart} = this.state
		if(cart.find(element => element.time === bike.time)){
			const newCart = cart.map(element => element.time === bike.element ? ({...element, quantity: element.quantity + 1}) : element)
			return this.setState({cart: newCart}) 
		}
		return this.setState({cart: this.state.cart.concat({...bike, quantity: 1})})
	}

	showCart = () => {
		console.log('mostrado')
	}

	render(){
		return(
			<div>
				<Bikes
				addToCart = {this.addToCart}
				bikes = {this.state.bikes}
				/>			
			</div>

		)
	}
}

export default App
