import { Component } from "react";
import CartBike from './CartBike'


const styles = {
	cartBox: {
		display: 'flex',
		
		
		alignItems: 'right',
		backgroundColor: '#fff',
		position: 'relative',
		
		boxShadow: '1px 5px 5px rgb(0,0,0,0.3)',
		borderRadius: '5px',
		width: '300px',
		left: 1000,
		top: 0
	},
	ul: {
		listStyleType: 'none',
		justifyContent: 'space-between',
		display: 'flex',
		position: 'relative',
		
	},
}

class CartBox extends Component{
	render(){
		const { cart, deleteBike, isBikeInCart, totalPrice } = this.props
		return(
			<div style={styles.cartBox}>

				<ul style={styles.ul}>
					{cart.map(cartBike => 
						<li key={cartBike.time}><CartBike 
						cartBike={cartBike}

						deleteBike={deleteBike}
						isBikeInCart={isBikeInCart}
						/></li>
					)}
					<li>{`${totalPrice}€`}</li>
				</ul>


			</div>
		)
	}

}

export default CartBox
