import { Component } from "react";
import Button from './Button.js'
import axios from "axios"

const styles = {
	bike: {
		position: 'relative',
		backgroundColor: '#FCFCFC',
		border: 'solid 1px grey',
		marginTop: 10,
		marginLeft: 50,
		boxShadow: '1px 5px 5px rgb(0,0,0,0.3)',
		borderRadius: '5px',
		width: '600px',
		height: '50px',
		display: 'flex',
		alignItems: 'center'
	},
	stock: {
		color: 'green',
		position: 'absolute',
		right: 90
	},
	time: {
		position: 'absolute',
		left: 20
	},
	price: {
		position: 'absolute',
		right: 125
	},
	button: {
		position: 'absolute',
		right: 20,
		cursor: 'pointer'
	}
	
}

class Bike extends Component {

	render(){
		const { bike, addToCart, showCart, updateStock, runOutStock, totalPrice, handleSubmit } = this.props
		/*const handleSubmit = async (e) => {
			e.preventDefault();
			try{
				const {data} = await axios.post('http://localhost:3001/test',
			{
				totalPrice: totalPrice
			},
			)
			console.log(data)
			} catch(error){
				console.log(error)
			}
		}*/
		
		return(
			<div style={runOutStock(bike)}>
				<h3 style={styles.time}>{bike.time}</h3>
				<div style={styles.stock}><h4>{bike.stock}</h4></div>
				
				<h4 style={styles.price}>{bike.price}</h4>
				{bike.isButtonVisible?
				<Button style={styles.button} onClick={()=>{addToCart(bike); showCart(); updateStock(bike); handleSubmit()}}>
					Book
				</Button> : null}
				
			</div>	
		)
	}
}

export default Bike
