import Bikes from './components/Bikes'
import { Component } from 'react';
import CartBox from './components/CartBox';

class App extends Component{

	state = {
		bikes: [
		{time: '09:00', stock: 8, price: '10€'},
		{time: '10:00', stock: 8, price: '10€'},
		{time: '11:00', stock: 8, price: '10€'},
		{time: '12:00', stock: 8, price: '10€'},
		{time: '13:00', stock: 8, price: '10€'},
		{time: '14:00', stock: 8, price: '10€'},
		{time: '15:00', stock: 8, price: '10€'},
		{time: '16:00', stock: 8, price: '10€'}],
		cart: [],
		isCartVisible: false
	}

	addToCart = (bike) => {
		const {cart, bikes} = this.state
		let checkStock;


		/*if(cart.find(element => element.time === bike.time)){
			const newCart = cart.map(element => element.time === bike.time ? 
				({...element, quantity: element.quantity + 1, stock: element.stock - 1}) : element)
			return this.setState({cart: newCart}) 
		}*/
		if(checkStock=cart.find(element => element.time === bike.time)){
			if(checkStock>0){
				const newCart = cart.map(element => element.time === bike.time ? 
					({...element, quantity: element.quantity + 1, stock: element.stock - 1}) : element)
				return this.setState({cart: newCart}) 
			}
		}

		return this.setState({cart: this.state.cart.concat({...bike, stock: bike.stock - 1, quantity: 1})})
	}

	updateStock = (bike) => {
		const {bikes} = this.state
		if(bikes.find(element => element.time === bike.time)){
			const newStock = bikes.map(element => element.time === bike.time ? 
				({...element, stock: element.stock - 1}) : element)
			return this.setState({bikes: newStock}) 
		}
	}

	showCart = () => {
		this.setState({isCartVisible:true})	
	}

	render(){
		//console.log(this.state.cart)
		console.log(this.state.bikes)
		const {isCartVisible} = this.state
		//const {bike} = this.props
		return(
			<div>
				{isCartVisible?<CartBox 
				cart={this.state.cart}
				showCart={this.state.showCart}
				isCartVisible={this.state.isCartVisible}/>:null}

				<Bikes
				addToCart = {this.addToCart}
				showCart = {this.showCart}
				bikes = {this.state.bikes}
				updateStock = {this.updateStock}
				/>





			</div>

		)
	}
}

export default App
