import { Component } from "react";
import CartBike from './CartBike'
import Button from './Button'

import CheckoutForm from './components/CheckoutForm'
import { loadStripe } from "@stripe/stripe-js";
import {Elements} from "@stripe/react-stripe-js";

const styles = {
	cartBox: {
		display: 'flex',
		alignItems: 'right',
		backgroundColor: '#FCFCFC',
		position:'absolute',
		boxShadow: '1px 5px 5px rgb(0,0,0,0.3)',
		borderRadius: '5px',
		width: '300px',
		left: 900,
		top: 90,
		paddingTop: 25
	},
	ul: {
		listStyleType: 'none'
	}
}



class CartBox extends Component{
	render(){
		const { cart, deleteBike, isBikeInCart, totalPrice, showCheckoutForm } = this.props
		return(
			<div style={styles.cartBox}>
				{<ul style={styles.ul}>
					{cart.map(cartBike => 
						<li key={cartBike.time}><CartBike 
						cartBike={cartBike}
						deleteBike={deleteBike}
						isBikeInCart={isBikeInCart}
						/></li>
					)}
					<li>{`Total: ${totalPrice}€`}</li>
        			<Button onClick={()=>showCheckoutForm()}>Book</Button>
					<Elements stripe={stripePromise}>
					 <CheckoutForm showCheckoutForm={this.showCheckoutForm}/>
					
			</Elements>
				</ul>}
			</div>
		)
	}

}

export default CartBox
