import { Component } from "react";
import Button from './Button'

const styles = {
	bike: {
		position: 'relative',
		backgroundColor: 'white',
		border: 'solid 1px grey',
		marginTop: 10,
		marginLeft: 50,
		boxShadow: '1px 5px 5px rgb(0,0,0,0.3)',
		borderRadius: '5px',
		width: '600px',
		height: '50px',
		cursor: 'pointer',
		display: 'flex',
		alignItems: 'center',
		justifyContent: 'space-between',
		
	},
	h4: {
		color: 'green',
		position: 'absolute',
		right: 90
	},
	h3: {
		position: 'absolute',
		left: 20
	},
	h5: {
		position: 'absolute',
		right: 125
	},
	button: {
		position: 'absolute',
		right: 20
	}
	
}

class Bike extends Component {

	render(){
		const { bike, addToCart, showCart, updateStock } = this.props
		return(
			<div style={styles.bike}>
				<h3 style={styles.h3}>{bike.time}</h3>
				<div style={styles.h4}><h4>{bike.stock}</h4></div>
				
				<h5 style={styles.h5}>{bike.price}</h5>
				{bike.isButtonVisible?<Button style={styles.button} onClick={()=>{addToCart(bike); showCart(); updateStock(bike)}}>
					Book
				</Button> : null}
				
			</div>	
		)
	}
}

export default Bike
