import { Component } from "react";
import Button from './Button'

const styles = {
	bike: {
		backgroundColor: 'white',
		border: 'solid 1px grey',
		marginTop: 10,
		marginLeft: 50,
		boxShadow: '1px 5px 5px rgb(0,0,0,0.3)',
		borderRadius: '5px',
		width: '600px',
		height: '50px',
		cursor: 'pointer',
		display: 'flex',
		alignItems: 'center',
	},
	h5: {
		alignItems: 'center',
		color: 'red'
	}
}

class Bike extends Component {

	render(){
		const { bike, addToCart } = this.props
		return(
			<div style={styles.bike}>
				<h4>{bike.time}</h4>
				<h5>{bike.quantity}</h5>
				<h5>{bike.price}</h5>
				<Button>
					Book
				</Button>
			</div>	
		)
	}
}

export default Bike
