import CartBike from './CartBike'


const styles = {
	cartBox: {
		display: 'flex',
		
		
		alignItems: 'right',
		backgroundColor: '#fff',
		position:'absolute',
		
		boxShadow: '1px 5px 5px rgb(0,0,0,0.3)',
		borderRadius: '5px',
		width: '300px',
		height: '50px',
		left: 900,
		top: 50
	},
	ul: {
		listStyleType: 'none',
		justifyContent: 'space-between',
		display: 'flex',
		position: 'relative',
		alignItems: 'center',
		
		
	},
	li: {
		position: 'absolute'
	}
}

class CartBox extends Component{
	render(){
		const { cart, deleteBike, isBikeInCart, totalPrice } = this.props
		return(
			<div style={styles.cartBox}>

				{/*<ul style={styles.ul}>
					{cart.map(cartBike => 
						<li style={styles.li} key={cartBike.time}><CartBike 
						cartBike={cartBike}

						deleteBike={deleteBike}
						isBikeInCart={isBikeInCart}
						/></li>
					)}
					<li>{`${totalPrice}â‚¬`}</li>
					</ul>*/}
					
					<table>
						{cart.map(cartBike => 
							<tr>
								<td><CartBike 
						cartBike={cartBike}

						deleteBike={deleteBike}
						isBikeInCart={isBikeInCart}
						/></td>
							</tr>)}
					</table>


			</div>
		)
	}

}

export default CartBox
