import React, { useState } from 'react'
import BookingScheduleNavBar from './BookingScheduleNavBar.js'
import AboutNavBar from './AboutNavBar.js'
import HomeNavBar from './HomeNavBar.js'
import SelectBike from './SelectBike.js'


const NavBar = () => {
	
	const [ isBookingSchedule, isBookingScheduleState ] = useState(false)
	const [ isAbout, isAboutState ] = useState(false)
	const [ isSelectBike, isSelectBikeState ] = useState(false)

	const isBookingScheduleUpdated = () => {
		if(isAbout){
			isAboutState(false)
		}
		return isBookingScheduleState(true)
	}
	const isAboutUpdated = () => {
		
		if(isBookingSchedule){
			isBookingScheduleState(false)
		}
		return isAboutState(true)
	}

	const isSelectBikeUpdated = () => {
		if(isAbout){
			isAboutState(false)
		}
		return isSelectBikeState(true)
	}
		

	const useHomeNavBar = () => {
		isBookingScheduleState(false)
		isAboutState(false)
	}
	
	if(isSelectBike) {
		return <SelectBikeNavBar 
		useHomeNavBar={useHomeNavBar}
		isAboutUpdated={isAboutUpdated}/>
	}
	if(isAbout) {
		return <AboutNavBar 
		useHomeNavBar={useHomeNavBar}
		isSelectBikeUpdated={isSelectBikeUpdated}/>
	}

	return <HomeNavBar 
		isAboutUpdated={isAboutUpdated}
		isSelectBikeUpdated={isSelectBikeUpdated}/>
}



export default NavBar
